  const key = '72316469';
export default function App() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [isLoading , setIsLoading] = useState(false);
  const [error , setError] = useState("");
  const [selectId , setSelectId] = useState(null);
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));
  function handleSelection(id){
    setSelectId(curretSelected => curretSelected === id ? null : id)
  }
  function closeMovieDetails(){
    setSelectId(null);
  }
  function handleAddWatchedMovie(movie){
    setWatched(watched=> [...watched , movie])
  }
  useEffect(function(){
    async function fetchMovie(){
      try{
      setIsLoading(true);
      const res = await fetch(`http://www.omdbapi.com/?apikey=${key}&s=${query}`)
      if(!res.ok) throw new Error("sometihng in your ass....")
      const data = await res.json();
    if(data.Response=== "False") throw new Error("Movie not found")
      setMovies(data.Search);

    }
      catch(err){
        setError(err.message);
      }finally{
        setIsLoading(false);
      }
      if(query.length < 3){
        setError("");
        setMovies([])
      }
    }
    fetchMovie();
  },[query])
  return (
    <>
       <NavBar>
        <SearchBox query={query} setQuery={setQuery}/>
        <MoviesLength movies={movies}/>
       </NavBar>
     <Main >
      <Box>
        {isLoading&& <Loader/>}
        {!isLoading && !error && <MoviesList handleSelection = {handleSelection} movies={movies}/>}
        {error && <ErrorBox errorMessage={error}/>}
      </Box>
      <Box>

      {/* <Summary watched = {watched} avgImdbRating={avgImdbRating} avgRuntime={avgRuntime} avgUserRating={avgUserRating} /> */}
      {selectId && <MovieDetails onClose = {closeMovieDetails} selectId={selectId}/>}
      < WatchedMovie watched={watched}/ >
      </Box>
     </Main>
     </>

  );
}
function Loader(){
  return <p className="loader">loading...</p>
}

function ErrorBox({errorMessage}){
  return <p className="error"> ⛔ {errorMessage} </p>
}

