const containerStyle={
display : "flex",
alignItems : "center",
gap : "16px"
}
const starContainerStyle = {
display : "flex",
gap : "8px"
}
const textStyle  = {
    lineHeight : "2",
    margin : "0"
}
export default function Stars({MaxRating = 5}){
return(<div style={containerStyle}>
<div style={starContainerStyle}>
{Array.from({length : MaxRating} , (_ , i)=>(<Star key={i}/>))}
</div>
<p style={textStyle}>10</p>
</div>)
}
const starStyle = {
    width : "48px",
    height : "48px",
    display : "block",
    cursor : "pointer"
}
function Star(){
return(<span role="button" s>
    <svg
  xmlns="http://www.w3.org/2000/svg"
  viewBox="0 0 20 20"
